<style lang="scss">
  .layout {
    min-height: 101vh;
    display: grid;
    grid-template-columns: auto 1fr;
    grid-template-rows: auto 1fr;
    overflow: hidden;
    & .circles {
      position: fixed;
      overflow: hidden;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      z-index: -1;
      & .circle {
        background: radial-gradient(50% 50% at 50% 50%, #bcbce6 1.04%, rgba(188, 188, 230, 0) 100%);
        position: absolute;
        &.c1 {
          width: 30rem;
          height: 30rem;
          top: 0%;
          left: 0%;
          animation: grow1 5s linear forwards;
          transform-origin: center;
          @media (min-width:$breakpoint-2) {
            width: 40rem;
            height: 40rem;
          }
          @media (min-width:$breakpoint-3) {
            width: 50rem;
            height: 50rem;
          }
        }
        &.c2 {
          width: 30rem;
          height: 30rem;
          top: calc(100% - 30rem);
          right: 0%;
          animation: grow2 5s linear forwards;
          transform-origin: center;
          @media (min-width:$breakpoint-2) {
            width: 40rem;
            height: 40rem;
            top: calc(100% - 40rem);
          }
          @media (min-width:$breakpoint-3) {
            width: 50rem;
            height: 50rem;
            top: calc(100% - 50rem);
          }
        }
      }
    }
    & .page {
      grid-area: 1 / 2 / span 2 / span 1;
      border-left: 1px solid white;
    }
    & .vertical {
      background-size: contain;
      margin: 1rem 1rem 0 1rem;
      overflow: hidden;
      grid-area: 1 / 1 / span 1 / span 1;
      width: 3rem;
    }
    & .vertical2 {
      margin: 0 1rem 1rem 1rem;
      overflow: hidden;
      grid-area: 2 / 1 / span 1 / span 1;
      width: 3rem;
      height: 100%;
      background-size: contain;
      background-repeat: repeat-y;
      background-image: url("/VPattern2.svg");
    }
    
  }
</style>
